{% block title %}Hello CartController!
{% endblock %}

{% block body %}
    <div class=\"bg-light p-3\">
        <h1>Votre panier</h1>
    </div>

    {% if items | length > 0  %}

    <table class=\"table\">
        <thead>

            <tr>
                <th> Produit</th>>
                <th> Prix </th>>
                <th> Quantite </th>>
                <th> Total </th>>
                <th> </th>>
        </thead>

        <tbody>
        {% for item in items %}
        <tr>
            <td>{{ item.produit.nom }} </td>
            <td>{{ item.produit.prix }} </td>
            <td>{{ item.quantite }} </td>
            <td>{{item.produit.prix * item.quantite  }} </td>
            <td>
                <a href=\"#\" class=\"btn btn_danger btn-sm\">
                   <i class=\"fas fa-trash\"></i>

                </a>
            </td>
        </tr>
        {% endfor %}
        </tbody>
    </table>
{% else %}

    <p>Surprise, c'est vide !</p>

    {% endif %}
{% endblock %}
